(5, 'donate', '{\"en\":\"donate\",\"es\":\"donar\"}', '{\"en\":\"Donate\",\"es\":\"Donar\"}', '{\"en\":\"Support our humanitarian efforts in South America\",\"es\":\"Apoye nuestros esfuerzos humanitarios en Sudamérica\"}', 'active', NULL, '{\"en\":\"<iframe src=\\\"https://donate.example.com/main/en\\\" width=\\\"100%\\\" height=\\\"600\\\"></iframe>\",\"es\":\"<iframe src=\\\"https://donate.example.com/main/es\\\" width=\\\"100%\\\" height=\\\"600\\\"></iframe>\"}', '{\"en\":\"<iframe src=\\\"https://player.vimeo.com/video/donate_en\\\" width=\\\"100%\\\" height=\\\"315\\\"></iframe>\",\"es\":\"<iframe src=\\\"https://player.vimeo.com/video/donate_es\\\" width=\\\"100%\\\" height=\\\"315\\\"></iframe>\"}', '{\"en\":{\"h1_color_title\":\"Donor Advised Funds\",\"h1_rest_title\":\"/South American Initiative\",\"paragraph_1\":\"The main reason DAF (Donor-advised funds) are the fastest-growing charitable giving vehicle in the United States because they are one of the easiest and most tax-advantageous ways to give to charity.\",\"text_title_1\":\"Your gift will help the South American Initiative:\",\"paragraph_2\":\"> Provide medicine, medical care, clean drinking water, emergency shelter, and other lifesaving support to Venezuelans caught in crisis > Provide education for abandoned and underprivileged children\",\"paragraph_3\":\"> Support resettled Venezuelans in the United States to help them adjust to their new country, learn English, find job opportunities, and gain control over their new lives\",\"paragraph_4\":\"> Provide safety and protection for the most vulnerable in Venezuela: Orphans, single mothers, senior citizens, and hospitalized patients\",\"text_title_2\":\"How (DAF) donor-advised funds work\",\"paragraph_5\":\"As a donor, you can direct your gifts through a donor-advised fund. Your contribution to qualifying DAFs is considered a charitable contribution by the Internal Revenue Service (IRS) at the time of the donation.\",\"paragraph_6\":\"This allows you the flexibility of advising the DAF when and to which charitable organizations you’d like to make a gift.\",\"text_title_3\":\"South American Initiative Provides Life-Saving Essentials\",\"paragraph_7\":\"South American Initiative puts your DAF to work immediately by providing food, medicine, and medical supplies to orphanages, hospitals, animal shelters and zoos.\"},\"es\":{\"h1_color_title\":\"\",\"h1_rest_title\":\"\",\"paragraph_1\":\"\",\"text_title_1\":\"\",\"paragraph_2\":\"\",\"paragraph_3\":\"\",\"paragraph_4\":\"\",\"text_title_2\":\"\",\"paragraph_5\":\"\",\"paragraph_6\":\"\",\"text_title_3\":\"\",\"paragraph_7\":\"\"}}', '{\"en\":{\"seo_title\":null,\"seo_description\":null,\"keywords\":null},\"es\":{\"seo_title\":null,\"seo_description\":null,\"keywords\":null}}', '2025-05-29 23:10:43', '2025-06-05 01:37:44'),
(6, 'crypto', '{\"en\":\"crypto\",\"es\":\"cripto\"}', '{\"en\":\"Crypto Donations\",\"es\":\"Donaciones en Cripto\"}', '{\"en\":\"Support our cause with cryptocurrency donations\",\"es\":\"Apoye nuestra causa con donaciones en criptomonedas\"}', 'active', NULL, '{\"en\":\"<iframe src=\\\"https://crypto.example.com/en\\\" width=\\\"100%\\\" height=\\\"600\\\"></iframe>\",\"es\":\"<iframe src=\\\"https://crypto.example.com/es\\\" width=\\\"100%\\\" height=\\\"600\\\"></iframe>\"}', '{\"en\":\"<iframe src=\\\"https://player.vimeo.com/video/crypto_en\\\" width=\\\"100%\\\" height=\\\"315\\\"></iframe>\",\"es\":\"<iframe src=\\\"https://player.vimeo.com/video/crypto_es\\\" width=\\\"100%\\\" height=\\\"315\\\"></iframe>\"}', '{\"en\":{\"h1_title\":\"How to Donate Cryptocurrency\",\"paragraph_1\":\"If you use Bitcoin to donate to charitable organizations, please send your bitcoin to help the orphans, hospital patients, and SAI Medical Clinic for Children. By putting your BitCoin to work, you will be helping to continue the growth and usage of this global digital currency.\",\"text_title\":\"What is SAI & How Does it Work?\",\"paragraph_2\":\"SAI (South American Initiative) is a U.S.-based non-profit 501 (c) (3), Non-Government Organization (NGO) focused on providing desperately needed humanitarian aid to the people of Venezuela.\",\"paragraph_3\":\"SAI is second only to the Red Cross when it comes to providing humanitarian aid during the current political and economic crisis in Venezuela.\",\"paragraph_4\":\"Your Bitcoin donations will help provide desperately needed food medicine, medical supplies, shoes, clothing, and professional care for those in their deepest time of need.\",\"contact_title\":\"Contact Us For More Infos\",\"contact_side_text_1\":\"\",\"contact_side_text_2\":\"SAI automatically generates a receipt at the time of your donation. We will send you a receipt if you choose to provide us with your contact details.\"},\"es\":{\"h1_title\":\"\",\"paragraph_1\":\"\",\"text_title\":\"\",\"paragraph_2\":\"\",\"paragraph_3\":\"\",\"paragraph_4\":\"\",\"contact_title\":\"\",\"contact_side_text_1\":\"\",\"contact_side_text_2\":\"\"}}', '{\"en\":{\"seo_title\":null,\"seo_description\":null,\"keywords\":null},\"es\":{\"seo_title\":null,\"seo_description\":null,\"keywords\":null}}', '2025-05-29 23:10:44', '2025-06-05 01:20:15'),
