(122, '{\"en\": \"life-saving-neurosurgery-venezuela\", \"es\": \"neurocirugias-para-salvar-vidas\"}', '{\"en\": \"Life-Saving Neurosurgery | Venezuela\", \"es\": \"Neurocirugías para salvar vidas\"}', '{\"en\": \"The South American Initiative has the only medical program offering help to the epileptic children of Venezuela. SAI is supporting life-saving neurosurgery by providing Vagus Nerve Stimulator Implants (VNS) that offer relief from daily life-threatening seizures for kids suffering form epilepsy. Second only to the Red Cross, SAI\'s field team provides immediate help where it is needed most, to those who cannot take care of themselves -- sick children and orphans.\", \"es\": null}', '{\"en\": \"<iframe src=\\\"https://donorbox.org/embed/life-saving-neurosurgery-venezuela\\\" height=\\\"685px\\\" width=\\\"100%\\\" style=\\\"max-width: 500px; min-width: 310px; max-height: none !important; height: 959px;\\\" seamless=\\\"seamless\\\" name=\\\"donorbox\\\" frameborder=\\\"0\\\" scrolling=\\\"no\\\" allowpaymentrequest=\\\"\\\" data-donorbox-id=\\\"DonorBox-f1\\\"></iframe>\", \"es\": null}', '{\"en\": null, \"es\": null}', '{\"en\": {\"special_tag\": \"Urgent Help\", \"description_heading\": \"Description Heading\", \"description_text\": \"Detailed Description\", \"summary_title\": \"Summary\", \"summary_text\": \"\", \"help_mini_title\": \"Help People In need\", \"help_title\": \"Start Donating Today, Make The Difference\", \"help_btn_text\": \"DONATE TO THIS PROJECT >\"}, \"es\": {\"special_tag\": \"Ayuda urgente\", \"description_heading\": \"Subtitulo por defecto\", \"description_text\": \"Texto de descripción por defecto\", \"summary_title\": \"Resumen\", \"summary_text\": \"\", \"help_mini_title\": \"Ayuda a personas en necesidad\", \"help_title\": \"Comienza a donar hoy, Haz la diferencia\", \"help_btn_text\": \"DONAR A ESTE PROYECTO >\"}}', '{}', '{\"en\": null, \"es\": \"\"}', '{}', 'published', NULL, 1, '2025-07-21 17:32:11', '2025-07-21 17:32:11'),
